import React, { useRef } from 'react'

var assetUrl = "";
var urlOfLastUpload = "";

// Define function to return the url of the uploaded image
export function uploadedAsset (imageUrl) {
    console.log(imageUrl)
    // const ref = useRef({
    //     assetUrl: "",
    //     urlOfLastUpload: ""
    // })

    // Record image url after upload
    if(!imageUrl === "") {
        urlOfLastUpload = imageUrl
    }    

    // Get url of last uploaded image
    if(imageUrl === ""){
        assetUrl = urlOfLastUpload
    } else {
        assetUrl = imageUrl
    }
    
    return assetUrl
}
