import { useEffect, useState } from "react";
import { 
  connectWallet,
  getCurrentWalletConnected,
  mintNFT
} from "./utils/interact.js";
import { ImageUpload } from 'react-ipfs-uploader'

const Minter = (props) => {

  //State variables
  const [walletAddress, setWallet] = useState("");
  const [status, setStatus] = useState("");
  const [name, setName] = useState("");
  const [collection, setCollection] = useState("");
  const [customTraitType1, setCustomTraitType1] = useState("");
  const [customTrait1, setCustomTrait1] = useState("");
  const [customTraitType2, setCustomTraitType2] = useState("");
  const [customTrait2, setCustomTrait2] = useState("");
  const [customTraitType3, setCustomTraitType3] = useState("");
  const [customTrait3, setCustomTrait3] = useState("");
  const [customTraitType4, setCustomTraitType4] = useState("");
  const [customTrait4, setCustomTrait4] = useState("");
  const [customTraitType5, setCustomTraitType5] = useState("");
  const [customTrait5, setCustomTrait5] = useState("");
  const [description, setDescription] = useState("");
  const [artist, setArtist] = useState("");
  const [imageUrl, setImageUrl] = useState("");

  useEffect(() => {
    async function fetchAccountData(){
      const {address, status} = await getCurrentWalletConnected();
      setWallet(address)
      setStatus(status);
    }
    fetchAccountData();

    addWalletListener();
  },[]);

  const connectWalletPressed = async () => { 
    const walletResponse = await connectWallet();
    setStatus(walletResponse.status);
    setWallet(walletResponse.address);
  };

  const onMintPressed = async () => { 
    const urlOfLastUpload = imageUrl;
    const imageCID = imageUrl.replace("https://ipfs.infura.io/ipfs/","")
    console.log(name, description, artist, urlOfLastUpload, imageCID)
    const { status } = await mintNFT( name, description, artist, urlOfLastUpload, imageCID);
    setStatus(status);
  };

  const  onAddAttributesPressed = async () => {
    return (
      <>
      <div class="Attributes">
        <form>
          <h4>🤔 Collection: </h4>
          <input
            type="text"
            placeholder="Put your cool NFT collection name here!!"
            onChange={(event) => setCollection(event.target.value)}
          />
          <h4>✍️ Trait Type 1: </h4>
          <input
            type="text"
            placeholder="e.g. Genre;)"
            onChange={(event) => setCustomTraitType1(event.target.value)}
          />
          <h4>✍️ Value: </h4>
          <input
            type="text"
            placeholder="e.g. Contemporary Art;)"
            onChange={(event) => setCustomTrait1(event.target.value)}
          /> 
          <h4>✍️ Trait Type 2: </h4>
          <input
            type="text"
            placeholder="e.g. Background;)"
            onChange={(event) => setCustomTraitType2(event.target.value)}
          />
          <h4>✍️ Value: </h4>
          <input
            type="text"
            placeholder="e.g. Green;)"
            onChange={(event) => setCustomTrait2(event.target.value)}
          />     
        </form>
        <button id="attributesButton" onClick={onMoreTraitsPressed}>
            Add More Traits
        </button>
      </div>
      </>
    );      
  }

  const  onMoreTraitsPressed = async () => {
    return (
      <>
      <div class="Attributes">
        <form>
          <h4>✍️ Trait Type 3: </h4>
          <input
            type="text"
            placeholder="e.g. Outfit;)"
            onChange={(event) => setCustomTraitType3(event.target.value)}
          />
          <h4>✍️ Value: </h4>
          <input
            type="text"
            placeholder="e.g. Tuxido;)"
            onChange={(event) => setCustomTrait3(event.target.value)}
          /> 
          <h4>✍️ Trait Type 4: </h4>
          <input
            type="text"
            placeholder="e.g. Face;)"
            onChange={(event) => setCustomTraitType4(event.target.value)}
          />
          <h4>✍️ Value: </h4>
          <input
            type="text"
            placeholder="e.g. Pirate;)"
            onChange={(event) => setCustomTrait4(event.target.value)}
          />
          <h4>✍️ Trait Type 5: </h4>
          <input
            type="text"
            placeholder="e.g. Special Effects;)"
            onChange={(event) => setCustomTraitType5(event.target.value)}
          />
          <h4>✍️ Value: </h4>
          <input
            type="text"
            placeholder="e.g. Access to exclusive event;)"
            onChange={(event) => setCustomTrait5(event.target.value)}
          />       
        </form>
      </div>
      </>
    );      
  }

  function addWalletListener() {
    if (window.ethereum) {
      window.ethereum.on("accountsChanged", (accounts) => {
        if (accounts.length > 0) {
          setWallet(accounts[0]);
          setStatus("👆🏽 Fill in the details of your NFT in the text-fields above.");
        } else {
          setWallet("");
          setStatus("🦊 Connect to Metamask using the top right button.");
        }
      });
    } else {
      setStatus(
        <p>
          {" "}
          🦊{" "}
          <a target="_blank" rel="noreferrer" href={`https://metamask.io/download.html`}>
            You must install Metamask, a virtual Ethereum wallet, in your browser.
          </a>
        </p>
      );
    }
  }

  return (
    <div className="Minter">
      <button id="walletButton" onClick={connectWalletPressed}>
        {walletAddress.length > 0 ? (
          "Connected: " +
          String(walletAddress).substring(0, 6) +
          "..." +
          String(walletAddress).substring(38)
        ) : (
          <span>Connect Wallet</span>
        )}
      </button>

      <br></br>
      <h1 id="title">🎨 Palette NFT Minter</h1>
      <p>
        Simply upload your asset/art; fill in the details for your NFT; then press "Mint."
      </p>
      
      <div>
        <h5> NFT ASSET UPLOAD </h5>
          <ImageUpload setUrl={setImageUrl} />
          <b>AssetUrl</b> : 
            <a
              href={imageUrl}
              target='_blank'
              rel='noopener noreferrer'
            >
              {imageUrl} 
            </a>
      </div>

      <form>
        <h2>🤔 Name: </h2>
        <input
          type="text"
          placeholder="e.g. My first NFT!"
          onChange={(event) => setName(event.target.value)}
        />
        <h2>✍️ Description: </h2>
        <input
          type="text"
          placeholder="e.g. Even cooler than cryptokitties ;)"
          onChange={(event) => setDescription(event.target.value)}
        />
        <h2>✍️ Artist: </h2>
        <input
          type="text"
          placeholder="e.g. Vincent Van Gogh;)"
          onChange={(event) => setArtist(event.target.value)}
        />     
      </form>
      <button id="attributesButton" onClick={onAddAttributesPressed}>
          Add Attributes
      </button>
      <button id="mintButton" onClick={onMintPressed}>
        Mint NFT
      </button>
      <p id="status">
        {status}
      </p>
    </div>
  );
};

export default Minter;
