const key = process.env.NFTPort_API_KEY;

var axios = require("axios").default;

export const NFTPortPinJSONToIPFS = (name, description, url) => {
  console.log(key);
  var options = {
    method: 'POST',
    url: 'https://api.nftport.xyz/v0/metadata',
    headers: {
      'Content-Type': 'application/json', 
      Authorization: '2d0719e3-a0ba-461f-a382-a5558bd86ac2'
    },
    data: {
      name: name,
      description: description,
      file_url: url,
    }
  }

  return axios
    .request(options).then(function (response) {
      console.log(response.data);
      return {
        success: true,
        url: response.metadata_url,
      };
    }).catch(function (error) {
    console.error(error);
    return {
      success: false,
      message: error.message,
    }
  });

};

export const NFTPortFileUpload = (image) => {

  var options = {
    method: 'POST',
    url: 'https://api.nftport.xyz/v0/files',
    headers: {
      'Content-Type': 'multipart/form-data',
      Authorization: '2d0719e3-a0ba-461f-a382-a5558bd86ac2',
      'content-type': 'multipart/form-data; boundary=---011000010111000001101001'
    },
    data: '-----011000010111000001101001\r\nContent-Disposition: form-data; name="file"\r\n\r\n\r\n-----011000010111000001101001--\r\n\r\n'
  };

  axios.request(options).then(function (response) {
    console.log(response.data);
    return {
      success: true,
      pinataUrl: "https://gateway.pinata.cloud/ipfs/" + response.ipfs_url
    };
  }).catch(function (error) {
    console.error(error);
    return {
      success: false,
      message: error.message,
    }
  });

}
