var axios = require("axios").default;

export const NFTPortPinJSONToIPFS = (name, description, url) => {
  var options = {
    method: 'POST',
    url: 'https://api.nftport.xyz/v0/metadata',
    headers: {
      'Content-Type': 'application/json', 
      Authorization: '2d0719e3-a0ba-461f-a382-a5558bd86ac2'
    },
    // data: {
    //   name: name,
    //   description: description,
    //   file_url: url,
    // }
    data: {
      "id":578,
      "name": name,
      "attributes":[
        {
          "trait_type":"Background",
          "value":"Red"
        },
        {
          "trait_type":"Special Effects",
          "value":"None"
        },
        {
          "trait_type":"Cats",
          "value":"B&W"
        },
        {
          "trait_type":"Outfit",
          "value":"Junk Food"
        },
        {
          "trait_type":"Face",
          "value":"Pirate"
        },
        {
          "trait_type":"Headgear",
          "value":"Astro Helmet"
        },
        {
          "trait_type":"tokenId",
          "value":578
        }
      ],
      "description":"10,000 Kitties that are over 21 with a Gamba Addiction ready to Degen their coins away for a Shot at Glory",
      "image":url
    }
  }

  return axios
    .request(options).then(function (response) {
      console.log(response.data);
      const url = response.data.metadata_uri;
      console.log(url);
      return {
        success: true,
        nftPortUrl: response.data.metadata_uri,
      };
    }).catch(function (error) {
    console.error(error);
    return {
      success: false,
      message: error.message,
    }
  });

};
