import React, { useRef } from 'react'

const imageUrl = "";
const urlOfLastUpload = "";

// Define function to return the url of the uploaded image
export function UploadedAsset (imageUrl) {
    console.log(imageUrl)
    const ref = useRef({
        assetUrl: "",
        urlOfLastUpload: ""
    })

    // Record image url after upload
    if(!imageUrl === "") {
        ref.current.urlOfLastUpload = imageUrl
    }    

    // Get url of last uploaded image
    if(imageUrl === ""){
        ref.current.assetUrl = urlOfLastUpload
    } else {
        ref.current.assetUrl = imageUrl
    }
    
    return assetUrl
}
