import {pinJSONToIPFS} from './pinata.js';
import { createReadStream } from 'fs';
import React, { useState } from 'react'

require('dotenv').config();
const { createAlchemyWeb3 } = require("@alch/alchemy-web3");
const web3 = createAlchemyWeb3("https://polygon-mumbai.g.alchemy.com/v2/pTB9jj_kefkrh3BSy6T-lOxFpLVS0mye");

const contractABI = require('../contract-abi.json');
const contractAddress = "0x5Fa62F03AF9cE30B684BAa9c9D7713CD64639dA8";

export const connectWallet = async () => {
  if (window.ethereum) {
    try {
      const addressArray = await window.ethereum.request({
        method: "eth_requestAccounts",
      });
      const obj = {
        status: "ğŸ‘†ğŸ½ Write a message in the text-field above.",
        address: addressArray[0],
      };
      return obj;
    } catch (err) {
      return {
        address: "",
        status: "ğŸ˜¥ " + err.message,
      };
    }
  } else {
    return {
      address: "",
      status: (
        <>
        <span>
          <p>
            {" "}
            ğŸ¦Š{" "}
            <a target="_blank" rel="noreferrer" href={`https://metamask.io/download.html`}>
              You must install Metamask, a virtual Ethereum wallet, in your
              browser.
            </a>
          </p>
        </span>
        </>
      ),
    };
  }
};

export const mintNFT = async(name, description, artist, urlOfLastUpload) => {
  //error handling
  if (urlOfLastUpload === "" || (name.trim() === "" || description.trim() === "" || artist.trim() === "")) { 
    return {
      success: false,
      status: "â—Please make sure all fields are completed before minting.",
    }
  }

  //make metadata
  const metadata = {
    "name": name,
    "image": urlOfLastUpload,
    "description": description,
    "attributes": [
      {
        "artist": artist
      }
    ]
  };
  // metadata.name = name;
  // metadata.image = urlOfLastUpload;
  // metadata.description = description;
  // metadata.artist = artist;
  
  console.log(metadata)
  
  //make pinata call
  const pinataResponse = await pinJSONToIPFS(metadata);
  if (!pinataResponse.success) {
    return {
      success: false,
      status: "ğŸ˜¢ Something went wrong while retrieving your tokenURI.",
    }
  } 

  // pin data and get asset url
  const tokenURI = pinataResponse.pinataUrl;

  //load the smart contract
  window.contract = await new web3.eth.Contract(contractABI, contractAddress);  

  //set up your Ethereum transaction
  const transactionParameters = {
    to: contractAddress, // Required except during contract publications.
    from: window.ethereum.selectedAddress, // must match user's active address.
    'data': window.contract.methods.mint(tokenURI).encodeABI()//make call to NFT smart contract 
  };

  //sign the transaction via Metamask
  try {
    const txHash = await window.ethereum
      .request({
        method: 'eth_sendTransaction',
        params: [transactionParameters],
      });
    return {
        success: true,
        status: "âœ… Check out your transaction on Polygonscan: https://mumbai.polygonscan.com//tx/" + txHash
    }
  } catch (error) {
    return {
      success: false,
      status: "ğŸ˜¥ Something went wrong: " + error.message
    }
  }
}

export const getCurrentWalletConnected = async () => {
  if (window.ethereum) {
    try {
      const addressArray = await window.ethereum.request({
        method: "eth_accounts",
      });
      if (addressArray.length > 0) {
        return {
          address: addressArray[0],
          status: "ğŸ‘†ğŸ½ Write a message in the text-field above.",
        };
      } else {
        return {
          address: "",
          status: "ğŸ¦Š Connect to Metamask using the top right button.",
        };
      }
    } catch (err) {
      return {
        address: "",
        status: "ğŸ˜¥ " + err.message,
      };
    }
  } else {
    return {
      address: "",
      status: (
        <span>
          <p>
            {" "}
            ğŸ¦Š{" "}
            <a target="_blank" rel="noreferrer" href={`https://metamask.io/download.html`}>
              You must install Metamask, a virtual Ethereum wallet, in your
              browser.
            </a>
          </p>
        </span>
      ),
    };
  }
};
