import './App.css';
import Minter, { checkMintComplete } from './Minter'

function App() {
  console.log(checkMintComplete)

  function showLinkToOpensea () {
    if(checkMintComplete){
      <p>Congrats! Your NFT will be available to view and trade on <a href="https://testnets.opensea.io/assets/">Opensea</a> shortly.</p>
    } else {
      <p>‚ùó Batch minting currently not supported!</p>
    }
  }

  return (
    <div>
      <div className="App">
        <Minter></Minter>
      </div>
      <div className="wrapper">
        { showLinkToOpensea }
      </div>
    </div>
    
  );
}

export default App;
