require('dotenv').config();
const key = process.env.REACT_APP_PINATA_KEY_0;
const secret = process.env.REACT_APP_PINATA_SECRET_0;
const jwt = process.env.REACT_APP_PINATA_JWT_0

const axios = require('axios');

export const pinJSONToIPFS = (JSONBody) => {
    const url = `https://api.pinata.cloud/pinning/pinFileToIPFS`;
    //making axios POST request to Pinata ⬇️
    console.log(jwt)
    return axios 
        .post(url, JSONBody, {
            headers: {
                'Content-Type': `multipart/form-data; boundary=${JSONBody._boundary}`,
                pinata_api_key: '198aa234fcba03dd1852',
                pinata_secret_api_key: 'ad10b8650d18187a2ca5c9a6cd022491936dce4f8135313a36ecf9b26a40820f',
                // Authorization: `Bearer ${jwt}`,
            }
        })
        .then(function (response) {
           return {
               success: true,
               pinataUrl: "https://gateway.pinata.cloud/ipfs/" + response.data.IpfsHash
           };
        })
        .catch(function (error) {
            console.log(error)
            return {
                success: false,
                message: error.message,
            }
    });
};
