        </span>
        </>
      ),
    };
  }
};

export const mintNFT = async(name, description, artist) => {
  console.log(imageUrl)
  //error handling
  if (imageUrl === "" || (name.trim() === "" || description.trim() === "" || artist.trim() === "")) { 
    return {
      success: false,
      status: "â—Please make sure all fields are completed before minting.",
    }
  }

  console.log(imageUrl)

  // const data = new FormData();
  // data.append('file', file, file.name);
  // console.log(file)

  // //make metadata
  // const metadata = JSON.stringify({
  //   name: name,
