import React, { useState } from 'react'

const urlOfLastUpload = ""
const assetUrl = ""

export function uploadedAsset (imageUrl) {

    if(!imageUrl === "") {
        urlOfLastUpload = imageUrl
    }    

    if(imageUrl === ""){
        assetUrl = urlOfLastUpload
    } else {
        assetUrl = imageUrl
    }

    return assetUrl
}
