import { useEffect, useState } from "react";
import { 
  connectWallet,
  getCurrentWalletConnected,
  mintNFT
} from "./utils/interact.js";
import { ImageUpload } from 'react-ipfs-uploader'

const Minter = (props) => {

  //State variables
  const [walletAddress, setWallet] = useState("");
  const [status, setStatus] = useState("");
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [artist, setArtist] = useState("");
  const [imageUrl, setImageUrl] = useState("");

  useEffect(() => {
    async function fetchAccountData(){
      const {address, status} = await getCurrentWalletConnected();
      setWallet(address)
      setStatus(status);
    }
    fetchAccountData();

    addWalletListener();
  },[]);

  const connectWalletPressed = async () => { 
    const walletResponse = await connectWallet();
    setStatus(walletResponse.status);
    setWallet(walletResponse.address);
  };

  const onMintPressed = async () => { 
    const urlOfLastUpload = imageUrl;
    const imageCID = imageUrl.replace("https://ipfs.infura.io/ipfs/","")
    console.log(name, description, artist, urlOfLastUpload, imageCID)
    const { status } = await mintNFT( name, description, artist, urlOfLastUpload);
    setStatus(status);
  };

  function addWalletListener() {
    if (window.ethereum) {
      window.ethereum.on("accountsChanged", (accounts) => {
        if (accounts.length > 0) {
          setWallet(accounts[0]);
          setStatus("üëÜüèΩ Fill in the details of your NFT in the text-fields above.");
        } else {
          setWallet("");
          setStatus("ü¶ä Connect to Metamask using the top right button.");
        }
      });
    } else {
      setStatus(
        <p>
          {" "}
          ü¶ä{" "}
          <a target="_blank" rel="noreferrer" href={`https://metamask.io/download.html`}>
            You must install Metamask, a virtual Ethereum wallet, in your browser.
          </a>
        </p>
      );
    }
  }

  return (
    <div className="Minter">
      <button id="walletButton" onClick={connectWalletPressed}>
        {walletAddress.length > 0 ? (
          "Connected: " +
          String(walletAddress).substring(0, 6) +
          "..." +
          String(walletAddress).substring(38)
        ) : (
          <span>Connect Wallet</span>
        )}
      </button>

      <br></br>
      <h1 id="title">üé® Palette NFT Minter</h1>
      <p>
        Simply upload your asset/art; fill in the details for your NFT; then press "Mint."
      </p>
      <form>
        <h2>ü§î Name: </h2>
        <input
          type="text"
          placeholder="e.g. My first NFT!"
          onChange={(event) => setName(event.target.value)}
        />
        <h2>‚úçÔ∏è Description: </h2>
        <input
          type="text"
          placeholder="e.g. Even cooler than cryptokitties ;)"
          onChange={(event) => setDescription(event.target.value)}
        />
        <h2>‚úçÔ∏è Artist: </h2>
        <input
          type="text"
          placeholder="e.g. Vincent Van Gogh;)"
          onChange={(event) => setArtist(event.target.value)}
        />
        <h2>‚úçÔ∏è Image: </h2>      
      </form>
      <div>
        <h5> NFT ASSET UPLOAD </h5>
          <ImageUpload setUrl={setImageUrl} />
          <b>AssetUrl</b> : 
            <a
              href={imageUrl}
              target='_blank'
              rel='noopener noreferrer'
            >
              {imageUrl} 
            </a>
      </div>
      <button id="mintButton" onClick={onMintPressed}>
        Mint NFT
      </button>
      <p id="status">
        {status}
      </p>
    </div>
  );
};

export default Minter;


